export function Button({ onClick, children, className }) {
  return (
    <button
      className={
        "flex justify-center items-center p-2.5 rounded bg-indigo-800 gap-2.5 text-white antialiased hover:bg-indigo-700 transition " +
        (className || "")
      }
      onClick={onClick}
    >
      {children}
    </button>
  );
}

